const copyId=document.querySelectorAll(".copy");copyId.forEach(el=>{el.addEventListener("click",event=>{navigator.clipboard.writeText(el.dataset.copy).then(()=>{$successSupplyToast='{ "type":"success" , '+'"header":"Копирование"  , '+'"message" : "Результат успешно скопирован в буфер обмена" }';createToast(JSON.parse($successSupplyToast));el.classList.add("opacity-25");setTimeout(()=>{el.classList.remove("opacity-25")},500)}).catch(err=>{console.log("Something went wrong",err)})})});