const scrollElements=document.querySelectorAll("[data-scroll]");scrollElements.forEach(element=>{element.addEventListener("click",()=>{const elementId=element.dataset.scroll;const position=element.dataset.scrollPosition||"center";scrollToElement(elementId,position);if(typeof bootstrap!=="object"){return}document.querySelectorAll('[data-bs-toggle="dropdown"]').forEach(menu=>{bootstrap.Dropdown.getInstance(menu)?.hide()});document.querySelectorAll(".offcanvas").forEach(menu=>{bootstrap.Offcanvas.getInstance(menu)?.hide()})})});function scrollToElement(elementId,position="center"){const element=document.getElementById(elementId);if(!element){console.error(`Элемент с ID "${elementId}" не найден!`);return}let scrollTop=0;switch(position){case"top":scrollTop=element.offsetTop;break;case"bottom":scrollTop=element.offsetTop+element.offsetHeight;break;case"center":default:scrollTop=element.offsetTop+element.offsetHeight/2-window.innerHeight/2;break}window.scrollTo({top:scrollTop,behavior:"smooth"})}